mapper.insert:

	SELECT IFNULL(MAX(board_id),1) AS board_id FROM BOARD

INSERT INTO IMAGE(IMAGE_SORT
				, IMAGE_FILE_ORIGINAL_NAME
				, IMAGE_FILE_NAME
				, IMAGE_DATE
				, BOARD_ID
				, MEMBER_NUM) 
			VALUE(#{image_sort}
				, #{image_file_original_name}
				, #{image_file_name}
				, NOW()
				, #{board_id}
				, #{member_num})

mapper.select:
SELECT IMAGE_ID , IMAGE_SORT , IMAGE_FILE_ORIGINAL_NAME , IMAGE_FILE_NAME , IMAGE_DATE , BOARD_ID , MEMBER_NUM FROM IMAGE WHERE 1=1
AND IMAGE_FILE_ORIGINAL_NAME = #{image_file_original_name}
AND IMAGE_SORT = #{image_sort}
AND BOARD_ID = #{board_id}
LIMIT 6
